import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import { Row, Col, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { DEVICE_ROUTE } from '../utils/consts';

const DeviceList = observer(() => {
  const { device } = useContext(Context);
  const navigate = useNavigate();

  if (!device.devices || device.devices.length === 0) {
    return (
      <div className="cyber-container text-center">
        <div className="cyber-card">
          <h3 className="text-neon-cyan">Товары не найдены</h3>
          <p className="text-muted">Попробуйте изменить фильтры поиска</p>
        </div>
      </div>
    );
  }

  return (
    <div className="cyber-container">
      <Row>
        {device.devices.map(device => (
          <Col xs={12} sm={6} lg={4} xl={3} key={device.id} className="mb-4">
            <Card 
              className="device-card h-100"
              onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}
            >
              <div className="position-relative overflow-hidden">
                <Card.Img
                  variant="top"
                  className="device-image"
                  src={process.env.REACT_APP_API_URL + device.img}
                  alt={device.name}
                  style={{ 
                    height: '200px', 
                    objectFit: 'cover',
                    transition: 'all 0.3s ease'
                  }}
                />
                <div 
                  className="position-absolute top-0 end-0 m-2 px-2 py-1 rounded"
                  style={{
                    background: 'rgba(0, 255, 255, 0.2)',
                    backdropFilter: 'blur(10px)',
                    border: '1px solid rgba(0, 255, 255, 0.3)'
                  }}
                >
                  <small className="text-neon-cyan fw-bold">
                    {device.brand?.name || 'Без бренда'}
                  </small>
                </div>
              </div>
              
              <Card.Body className="device-info d-flex flex-column">
                <div className="flex-grow-1">
                  <div className="device-brand mb-1">
                    {device.brand?.name || 'Без бренда'}
                  </div>
                  <Card.Title className="device-name mb-3">
                    {device.name}
                  </Card.Title>
                </div>
                
                <div className="mt-auto">
                  <div className="device-price mb-2">
                    {device.price}₽
                  </div>
                  <div className="d-flex justify-content-between align-items-center">
                    <small className="text-muted">
                      <i className="fas fa-eye me-1"></i>
                      Подробнее
                    </small>
                    <div className="text-neon-cyan">
                      <i className="fas fa-arrow-right"></i>
                    </div>
                  </div>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
});

export default DeviceList;
