import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import { Context } from '../index';

const BrandBar = observer(() => {
    const { device } = useContext(Context);

    return (
        <Row className="d-flex">
            {device.brands.map(brand => (
                <Col
                    key={brand.id}
                    // Adjust column size based on brand name length
                    xs={brand.name.length > 15 ? 12 : 6} // If name is long, take full width
                    sm={brand.name.length > 15 ? 12 : 4}
                    md={brand.name.length > 15 ? 6 : 3}  // Adjust for medium screens
                    lg={brand.name.length > 15 ? 4 : 2}  // Adjust for large screens
                    className="mb-3"
                >
                    <Card
                        style={{
                            cursor: 'pointer',
                            border: brand.id === device.selectedBrand.id ? '2px solid #007bff' : '1px solid #ddd',
                            borderRadius: '0.5rem',
                            boxShadow: '0 0.125rem 0.25rem rgba(0, 0, 0, 0.075)',
                            transition: 'border-color 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'center',
                            alignItems: 'center',
                            padding: '1rem',
                            textAlign: 'center',
                            wordBreak: 'break-word', // Prevent overflow for very long names
                        }}
                        onClick={() => device.setSelectedBrand(brand)}
                    >
                        {brand.name}
                    </Card>
                </Col>
            ))}
        </Row>
    );
});

export default BrandBar;
