import {makeAutoObservable} from 'mobx';

export default class UserStore {
    constructor() {
        this._isAuth = false;
        this._user = {};
        makeAutoObservable(this);
    }
    async checkAuth() {
        try {
            const userData = await check();
            if (userData) {
                this.setUser(userData);
                this.setIsAuth(true);
            }
        } catch (e) {
            this.setIsAuth(false);
        }
    }

    setIsAuth(bool) {
        this._isAuth = bool; 
    }
     
    setUser(user) {
        this._user = user; 
    }

    get isAuth() {
        return this._isAuth
    }

    get user() {
        return this._user
    }
}
