import React, { useEffect, useState, useContext } from 'react';
import { Container, Col, Image, Row, Button, Card } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchOneDevice, fetchBrands, deleteDevice } from '../http/deviceAPI';
import { addToBasket } from '../http/basketAPI';
import { Context } from '../index';

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const { user } = useContext(Context);
    const navigate = useNavigate();

    useEffect(() => {
        const load = async () => {
            try {
                const deviceData = await fetchOneDevice(id);
                const brands = await fetchBrands();
                const brand = brands.find(b => b.id === deviceData.brandId);

                setDevice({ ...deviceData, brand });
            } catch (error) {
                console.error('Ошибка при загрузке устройства:', error);
            }
        };

        load();
    }, [id]);

    const handleDelete = async () => {
        if (window.confirm('Вы точно хотите удалить этот товар?')) {
            try {
                await deleteDevice(id);
                alert('Товар успешно удалён');
                navigate('/'); // Вернуться в магазин
            } catch (error) {
                alert('Ошибка при удалении товара');
                console.error(error);
            }
        }
    };

    return (
        <Container style={{ maxWidth: 1200, paddingTop: '4rem', paddingBottom: '4rem' }}>

            <Row className="gx-5 align-items-start mb-5" style={{ gap: '2rem' }}>
                {/* Image and Brand/Name */}
                <Col xs={12} md={5} className="d-flex flex-column align-items-center gap-3">
                    <h1 style={{ fontWeight: 700, fontSize: '2.8rem', lineHeight: 1.1, color: '#111827', textAlign: 'center' }}>
                        {device.name}
                    </h1>
                    <p style={{ color: '#6b7280', fontSize: '1rem', marginBottom: '0', fontWeight: 500 }}>
                        Бренд: {device.brand?.name}
                    </p>
                    <Image
                        src={process.env.REACT_APP_API_URL + device.img}
                        alt={device.name}
                        fluid
                        style={{
                            maxHeight: 400,
                            borderRadius: '12px',
                            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.08)',
                            objectFit: 'contain',
                            width: '100%',
                            maxWidth: 400,
                        }}
                    />
                </Col>

                {/* Pricing and Actions */}
                <Col xs={12} md={5} lg={4}>
                    <Card
                        className="p-4 d-flex flex-column justify-content-center"
                        style={{
                            height: '100%',
                            borderRadius: 12,
                            boxShadow: '0 6px 16px rgba(0,0,0,0.04)',
                            border: '1px solid #e0e7ff',
                            backgroundColor: '#fff',
                        }}
                    >
                        <h2 style={{ marginBottom: '1.5rem', fontWeight: 700, fontSize: '2rem', color: '#111827', textAlign: 'center' }}>
                            От: {device.price} &#8381;
                        </h2>

                        <Button
                            variant="dark"
                            size="lg"
                            style={{ fontWeight: 600, borderRadius: 8 }}
                            onClick={async () => {
                                if (!user.isAuth) {
                                    alert('Сначала войдите в аккаунт, чтобы добавить товар в корзину');
                                    return;
                                }
                                try {
                                    await addToBasket(device.id);
                                    alert('Товар добавлен в корзину');
                                } catch (error) {
                                    alert('Ошибка при добавлении в корзину');
                                }
                            }}
                            className="mb-3"
                        >
                            Добавить в корзину
                        </Button>

                        {user.user?.role === 'ADMIN' && (
                            <div className="d-flex flex-column gap-3">
                                <Button
                                    variant="outline-secondary"
                                    size="md"
                                    style={{ fontWeight: 600, borderRadius: 8 }}
                                    onClick={() => navigate(`/edit-device/${device.id}`)}
                                >
                                    Редактировать товар
                                </Button>
                                <Button
                                    variant="outline-danger"
                                    size="md"
                                    style={{ fontWeight: 600, borderRadius: 8 }}
                                    onClick={handleDelete}
                                >
                                    Удалить товар
                                </Button>
                            </div>
                        )}
                    </Card>
                </Col>
            </Row>

            {/* Characteristics */}
            <Row style={{ marginTop: '3rem' }}>
                <Col xs={12} md={10} lg={9} className="mx-auto">
                    <h2 style={{ fontWeight: 700, fontSize: '2rem', marginBottom: '1.5rem', color: '#111827' }}>
                        Характеристики
                    </h2>
                    <div
                        style={{
                            maxHeight: '400px',
                            overflowY: 'auto',
                            borderRadius: '12px',
                            padding: '1rem',
                            backgroundColor: '#f9fafb',
                            boxShadow: 'inset 0 0 8px rgba(0,0,0,0.03)',
                            border: '1px solid #e0e7ff',
                        }}
                    >
                        {device.info.length > 0 ? (
                            device.info.map((info, index) => (
                                <Row
                                    key={info.id}
                                    style={{
                                        backgroundColor: index % 2 === 0 ? '#ffffff' : '#f3f4f6',
                                        padding: '0.75rem 1rem',
                                        borderRadius: 8,
                                        alignItems: 'center',
                                        gap: '1rem',
                                    }}
                                >
                                    <Col xs={5} sm={4} md={3} style={{ fontWeight: 600, color: '#4b5563', fontSize: '1rem' }}>
                                        {info.title}
                                    </Col>
                                    <Col xs={7} sm={8} md={9} style={{ fontSize: '1rem', color: '#374151' }}>
                                        {info.description}
                                    </Col>
                                </Row>
                            ))
                        ) : (
                            <p style={{ color: '#6b7280', fontSize: '1rem' }}>
                                Нет характеристик для данного устройства.
                            </p>
                        )}
                    </div>
                </Col>
            </Row>
        </Container>
    );
};

export default DevicePage;
