import { observer } from 'mobx-react-lite';
import { useContext } from 'react';
import { Context } from '../index';
import { useNavigate } from 'react-router-dom';
import { DEVICE_ROUTE } from '../utils/consts';
import { Col, Card, Image } from 'react-bootstrap';

const DeviceItem = observer(({ device }) => {
    const { device: deviceStore } = useContext(Context);
    const navigate = useNavigate();

    // Находим бренд по ID из общего списка брендов
    const brand = deviceStore.brands.find(b => b.id === device.brandId);

    return (
        <Col md={3} className="mb-4" onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}>
            <Card
                style={{
                    width: '100%',
                    cursor: 'pointer',
                    border: 'none',
                    borderRadius: '0.75rem',
                    boxShadow: '0 0.25rem 0.5rem rgba(0, 0, 0, 0.08)',
                    transition: 'transform 0.2s ease-in-out',
                }}
                onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'scale(1)';
                }}
            >
                <Image
                    variant="top"
                    src={process.env.REACT_APP_API_URL + device.img}
                    style={{
                        width: '100%',
                        height: 150,
                        objectFit: 'cover',
                        borderTopLeftRadius: '0.75rem',
                        borderTopRightRadius: '0.75rem',
                    }}
                />
                <Card.Body className="p-3">
                    <Card.Title style={{ fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '0.25rem' }}>
                        {device.name}
                    </Card.Title>
                    <Card.Text className="text-muted" style={{ fontSize: '0.9rem' }}>
                        {brand ? brand.name : 'Бренд не указан'}
                    </Card.Text>
                </Card.Body>
            </Card>
        </Col>
    );
});

export default DeviceItem;
