import React, { useContext, useState } from 'react';
import { Button, Card, Container, Row, Form, Col } from 'react-bootstrap';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE } from '../utils/consts';
import { registration, login } from '../http/userAPI';
import { observer } from 'mobx-react-lite';
import { Context } from '..';
import ReCAPTCHA from 'react-google-recaptcha';
import './Auth.css';

const Auth = observer(() => {
  const { user } = useContext(Context);
  const location = useLocation();
  const navigate = useNavigate();
  const isLogin = location.pathname === LOGIN_ROUTE;

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('USER');
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [captchaValue, setCaptchaValue] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [focusedInput, setFocusedInput] = useState('');

  const handleCaptchaChange = (value) => {
    setCaptchaValue(value);
  };

  const click = async () => {
    if (loginAttempts >= 2 && !captchaValue) {
      alert("Пожалуйста, пройдите капчу");
      return;
    }

    setIsLoading(true);
    try {
      let data;
      if (isLogin) {
        data = await login(email, password);
      } else {
        data = await registration(email, password, role);
      }
      user.setUser(data);
      user.setIsAuth(true);
      navigate(SHOP_ROUTE);
    } catch (e) {
      setLoginAttempts(prev => prev + 1);
      alert(e.response?.data?.message || "Ошибка входа");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-bg-effects">
        <div className="grid-overlay"></div>
        <div className="floating-particles">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="particle" style={{
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}></div>
          ))}
        </div>
      </div>

      <Container className="auth-content">
        <div className="auth-card">
          <div className="auth-header">
            <div className="cyber-title">
              <span className="glitch" data-text={isLogin ? 'АВТОРИЗАЦИЯ' : 'РЕГИСТРАЦИЯ'}>
                {isLogin ? 'АВТОРИЗАЦИЯ' : 'РЕГИСТРАЦИЯ'}
              </span>
            </div>
            <div className="scan-line"></div>
          </div>

          <Form className="auth-form">
            <div className="input-group-cyber">
              <div className={`input-wrapper ${focusedInput === 'email' ? 'focused' : ''} ${email ? 'filled' : ''}`}>
                <Form.Control
                  className="cyber-input"
                  placeholder="EMAIL_ADDRESS"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  onFocus={() => setFocusedInput('email')}
                  onBlur={() => setFocusedInput('')}
                  type="email"
                />
                <div className="input-border"></div>
                <div className="input-glow"></div>
              </div>
            </div>

            <div className="input-group-cyber">
              <div className={`input-wrapper ${focusedInput === 'password' ? 'focused' : ''} ${password ? 'filled' : ''}`}>
                <Form.Control
                  className="cyber-input"
                  placeholder="PASSWORD"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  onFocus={() => setFocusedInput('password')}
                  onBlur={() => setFocusedInput('')}
                  type="password"
                />
                <div className="input-border"></div>
                <div className="input-glow"></div>
              </div>
            </div>

            {!isLogin && (
              <div className="role-selection">
                <div className="role-title">ВЫБЕРИТЕ РОЛЬ:</div>
                <div className="role-options">
                  <div className={`role-option ${role === 'USER' ? 'active' : ''}`} onClick={() => setRole('USER')}>
                    <div className="role-radio">
                      <div className="radio-core"></div>
                    </div>
                    <span>ПОЛЬЗОВАТЕЛЬ</span>
                  </div>
                  <div className={`role-option ${role === 'ADMIN' ? 'active' : ''}`} onClick={() => setRole('ADMIN')}>
                    <div className="role-radio">
                      <div className="radio-core"></div>
                    </div>
                    <span>АДМИНИСТРАТОР</span>
                  </div>
                </div>
              </div>
            )}

            {isLogin && loginAttempts >= 2 && (
              <div className="captcha-container">
                <div className="captcha-wrapper">
                  <ReCAPTCHA
                    sitekey="6LfCyFkrAAAAAMYR4D_UmTKyyZb1UXWgEONhJXRo"
                    onChange={handleCaptchaChange}
                    theme="dark"
                  />
                </div>
              </div>
            )}

            <div className="auth-actions">
              <div className="auth-link">
                {isLogin ? (
                  <span>
                    НЕТ АККАУНТА? <NavLink to={REGISTRATION_ROUTE} className="cyber-link">РЕГИСТРАЦИЯ</NavLink>
                  </span>
                ) : (
                  <span>
                    ЕСТЬ АККАУНТ? <NavLink to={LOGIN_ROUTE} className="cyber-link">ВХОД</NavLink>
                  </span>
                )}
              </div>

              <div className="button-group">
                <button 
                  className={`cyber-button primary ${isLoading ? 'loading' : ''}`}
                  onClick={click}
                  disabled={isLoading}
                >
                  <span className="button-text">
                    {isLoading ? 'ОБРАБОТКА...' : (isLogin ? 'ВОЙТИ' : 'СОЗДАТЬ АККАУНТ')}
                  </span>
                  <div className="button-border"></div>
                  <div className="button-glow"></div>
                </button>

                {isLogin && (
                  <button
                    className="cyber-button secondary"
                    onClick={() => navigate(SHOP_ROUTE)}
                  >
                    <span className="button-text">ГОСТЕВОЙ РЕЖИМ</span>
                    <div className="button-border"></div>
                    <div className="button-glow"></div>
                  </button>
                )}
              </div>
            </div>
          </Form>
          
          <div className="auth-footer">
            <div className="status-bar">
              <div className="status-dot active"></div>
              <span>СИСТЕМА АКТИВНА</span>
            </div>
          </div>
        </div>
      </Container>
    </div>
  );
});

export default Auth;
