import React, { useContext, useState } from 'react';
import { Button, Card, Container, Row, Form, Col } from 'react-bootstrap';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE } from '../utils/consts';
import { registration, login } from '../http/userAPI';
import { observer } from 'mobx-react-lite';
import { Context } from '..';
import ReCAPTCHA from 'react-google-recaptcha';
import './Auth.css'; // Создайте этот файл для кастомных стилей

const Auth = observer(() => {
  const { user } = useContext(Context);
  const location = useLocation();
  const navigate = useNavigate();
  const isLogin = location.pathname === LOGIN_ROUTE;

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('USER');
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [captchaValue, setCaptchaValue] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleCaptchaChange = (value) => {
    setCaptchaValue(value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const click = async () => {
    try {
      if (loginAttempts >= 2 && !captchaValue) {
        alert("Пожалуйста, подтвердите, что вы не робот");
        return;
      }

      setIsLoading(true);
      let data;
      if (isLogin) {
        data = await login(email, password);
      } else {
        data = await registration(email, password, role);
      }
      user.setUser(data);
      user.setIsAuth(true);
      navigate(SHOP_ROUTE);
    } catch (e) {
      setLoginAttempts(prev => prev + 1);
      alert(e.response?.data?.message || (isLogin ? "Ошибка входа" : "Ошибка регистрации"));
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-background">
      <Container className="d-flex justify-content-center align-items-center min-vh-100">
        <Card className="auth-card shadow-lg">
          <Card.Body className="p-5">
            <div className="text-center mb-4">
              <h2 className="auth-title">{isLogin ? 'Добро пожаловать!' : 'Создать аккаунт'}</h2>
              <p className="text-muted">
                {isLogin ? 'Войдите в свой аккаунт' : 'Зарегистрируйтесь, чтобы начать'}
              </p>
            </div>

            <Form className="auth-form">
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  className="auth-input"
                  placeholder="your@email.com"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  type="email"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Пароль</Form.Label>
                <div className="password-input-group">
                  <Form.Control
                    className="auth-input"
                    placeholder="Введите пароль"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    type={showPassword ? "text" : "password"}
                  />
                  <Button 
                    variant="link" 
                    className="password-toggle"
                    onClick={togglePasswordVisibility}
                  >
                    <i className={`bi ${showPassword ? 'bi-eye-slash' : 'bi-eye'}`}></i>
                  </Button>
                </div>

              </Form.Group>

              {!isLogin && (
                <Form.Group className="mb-4">
                  <Form.Label>Роль</Form.Label>
                  <div className="d-flex gap-3">
                    <Button
                      variant={role === 'USER' ? 'primary' : 'outline-primary'}
                      onClick={() => setRole('USER')}
                      className="flex-grow-1"
                    >
                      Пользователь
                    </Button>
                    <Button
                      variant={role === 'ADMIN' ? 'primary' : 'outline-primary'}
                      onClick={() => setRole('ADMIN')}
                      className="flex-grow-1"
                    >
                      Администратор
                    </Button>
                  </div>
                </Form.Group>
              )}

              {isLogin && loginAttempts >= 2 && (
                <div className="mb-3">
                  <ReCAPTCHA
                    sitekey="6LfCyFkrAAAAAMYR4D_UmTKyyZb1UXWgEONhJXRo"
                    onChange={handleCaptchaChange}
                    className="w-100"
                  />
                </div>
              )}

              <Button
                variant="primary"
                onClick={click}
                className="auth-submit-btn w-100 mb-3"
                disabled={isLoading}
              >
                {isLoading ? (
                  <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                ) : isLogin ? (
                  'Войти'
                ) : (
                  'Зарегистрироваться'
                )}
              </Button>

              {isLogin && (
                <Button
                  variant="outline-secondary"
                  onClick={() => navigate(SHOP_ROUTE)}
                  className="w-100 mb-3"
                >
                  Продолжить как гость
                </Button>
              )}

              <div className="text-center mt-3">
                <span className="text-muted">
                  {isLogin ? 'Ещё нет аккаунта? ' : 'Уже есть аккаунт? '}
                  <NavLink 
                    to={isLogin ? REGISTRATION_ROUTE : LOGIN_ROUTE} 
                    className="auth-switch-link"
                  >
                    {isLogin ? 'Создать' : 'Войти'}
                  </NavLink>
                </span>
              </div>

              <div className="auth-divider my-4">
                <span className="px-2 text-muted">или</span>
              </div>

              <div className="d-flex justify-content-center gap-3">
                <Button variant="outline-primary" className="social-btn">
                  <i className="bi bi-google"></i>
                </Button>
                <Button variant="outline-primary" className="social-btn">
                  <i className="bi bi-facebook"></i>
                </Button>
                <Button variant="outline-primary" className="social-btn">
                  <i className="bi bi-github"></i>
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
});

export default Auth;
