import React, { useContext } from 'react';
import { Navbar, Nav, Button, Container } from 'react-bootstrap';
import { Context } from '../index';
import { useNavigate, useLocation } from 'react-router-dom';
import { ADMIN_ROUTE, LOGIN_ROUTE, SHOP_ROUTE, BASKET_ROUTE, REGISTRATION_ROUTE } from '../utils/consts';
import { observer } from 'mobx-react-lite';

const NavBar = observer(() => {
    const { user, device } = useContext(Context);
    const navigate = useNavigate();
    const location = useLocation();

    const logOut = () => {
        user.setUser({});
        user.setIsAuth(false);
        localStorage.removeItem('token');
        navigate(SHOP_ROUTE);
    };

    const handleHomeClick = () => {
        if (location.pathname === LOGIN_ROUTE || location.pathname === REGISTRATION_ROUTE) {
            return;
        }
        device.resetFilters();
        navigate(SHOP_ROUTE);
    };

    return (
        <Navbar
            bg="white" // Changed to white background
            variant="light" // Changed to light variant
            expand="lg"
            className="shadow-sm py-3" // Added shadow and padding
            style={{ borderBottom: '1px solid #e5e7eb' }} // Added subtle border
        >
            <Container>
                <Navbar.Brand
                    style={{
                        cursor: 'pointer',
                        fontWeight: 700, // Bold font weight
                        fontSize: '1.75rem', // Increased font size
                        color: '#374151', // Darker text color
                    }}
                    onClick={handleHomeClick}
                >
                    ТехноСфера
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav" className="justify-content-end"> {/* Align items to the right */}
                    <Nav style={{ alignItems: 'center' }}> {/* Vertically center items */}
                        {user.isAuth ? (
                            <>
                                {user.user.role === 'ADMIN' && (
                                    <Button
                                        variant="outline-primary" // Changed to primary color
                                        className="me-3 rounded-pill px-3" // Added rounded corners and padding
                                        onClick={() => navigate(ADMIN_ROUTE)}
                                    >
                                        Админ Панель
                                    </Button>
                                )}
                                <Button
                                    variant="outline-primary" // Changed to primary color
                                    className="me-3 rounded-pill px-3" // Added rounded corners and padding
                                    onClick={() => navigate(BASKET_ROUTE)}
                                    style={{ position: 'relative' }} // For badge positioning
                                >
                                    Корзина
                                    {/* Example badge - replace with actual cart count */}
                                    {/* <span
                                        className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger"
                                        style={{ fontSize: '0.7rem' }}
                                    >
                                        9+
                                    </span> */}
                                </Button>
                                <Button
                                    variant="primary" // Changed to primary color
                                    onClick={logOut}
                                    className="rounded-pill px-3" // Added rounded corners and padding
                                >
                                    Выйти
                                </Button>
                            </>
                        ) : (
                            <Button
                                variant="primary" // Changed to primary color
                                onClick={() => navigate(LOGIN_ROUTE)}
                                className="rounded-pill px-3" // Added rounded corners and padding
                            >
                                Авторизация
                            </Button>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
});

export default NavBar;
