import {makeAutoObservable} from 'mobx';

export default class UserStore {
    constructor() {
        this._isAuth = false;
        this._user = {};
        makeAutoObservable(this);
    }

    // Добавляем явную проверку роли
    isAdmin() {
        return this._user.role === 'ADMIN';
    }

    setIsAuth(bool) {
        this._isAuth = bool; 
    }
     
    setUser(user) {
        this._user = user; 
    }

    get isAuth() {
        return this._isAuth
    }

    get user() {
        return this._user
    }
}
