import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { Context } from '../index';
import { useNavigate } from 'react-router-dom';
import { DEVICE_ROUTE } from '../utils/consts';
import { Col, Card, Image } from 'react-bootstrap';

const DeviceItem = observer(({ device }) => {
    const { device: deviceStore } = useContext(Context);
    const navigate = useNavigate();

    // Находим бренд по ID из общего списка брендов
    const brand = deviceStore.brands.find(b => b.id === device.brandId);

    return (
        <Col
            xs={6}
            sm={4}
            md={3}
            lg={2}
            style={{
                marginBottom: '1rem',
                cursor: 'pointer',
                transition: 'transform 0.2s ease-in-out',
            }}
            onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}
            onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.03)';
            }}
            onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(2)';
            }}
        >
            <Card
                style={{
                    border: 'none',
                    borderRadius: '0.75rem',
                    boxShadow: '0 0.25rem 0.5rem rgba(0, 0, 0, 0.05)',
                    overflow: 'hidden', // Ensure image doesn't overflow
                }}
            >
                <div
                    style={{
                        position: 'relative',
                        width: '100%',
                        paddingBottom: '100%', // Maintain 1:1 aspect ratio
                    }}
                >
                    <Image
                        src={process.env.REACT_APP_API_URL + device.img}
                        style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            width: '100%',
                            height: '100%',
                            objectFit: 'cover', // Cover the container
                            transition: 'transform 0.3s ease',
                        }}
                        alt={device.name}
                    />
                </div>
                <Card.Body className="p-3">
                    <Card.Title
                        style={{
                            fontSize: '1rem',
                            fontWeight: 600,
                            marginBottom: '0.25rem',
                            color: '#374151',
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                        }}
                    >
                        {device.name}
                    </Card.Title>
                    <Card.Text
                        className="text-muted"
                        style={{
                            fontSize: '0.85rem',
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                        }}
                    >
                        {brand ? brand.name : 'Бренд не указан'}
                    </Card.Text>
                </Card.Body>
            </Card>
        </Col>
    );
});

export default DeviceItem;
