import React, { useContext } from 'react';
import { authRoutes, publicRoutes } from '../routes';
import { SHOP_ROUTE, LOGIN_ROUTE } from '../utils/consts';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Context } from "../index";
import { observer } from 'mobx-react-lite';

const AppRouter = observer(() => {
    const { user } = useContext(Context);

    return (
        <Routes>
            {/* Защищённые маршруты */}
            {user.isAuth
                ? authRoutes.map(({ path, Component }) =>
                    <Route key={path} path={path} element={<Component />} exact />
                )
                : null
            }

            {/* Публичные маршруты (логин, регистрация, и т.п.) */}
            {publicRoutes.map(({ path, Component }) =>
                <Route key={path} path={path} element={<Component />} exact />
            )}

            {/* По умолчанию: если авторизован — на магазин, иначе — на логин */}
            <Route path="*" element={
                user.isAuth
                    ? <Navigate to={SHOP_ROUTE} replace />
                    : <Navigate to={LOGIN_ROUTE} replace />
            } />
        </Routes>
    );
});

export default AppRouter;
