import React from 'react';
import { Container, Col, Image, Row, Button, Card} from 'react-bootstrap';

const DevicePage = () => {
    const device = {id: 1, name: 'KSV36AWEP', price: '127999', img: `0aeb8782-5544-4d2e-bce8-c6755a5040eb.jpg`};
    const description = [
        {id: 1, title: 'Оперативная память', description: '5 гб'},
        {id: 2, title: 'Камера', description: '12 мп'},
        {id: 3, title: 'Процессор', description: 'Пентиум 3'},
        {id: 4, title: 'Кол-во ядер', description: '2'},
        {id: 5, title: 'Аккумулятор', description: '4000 mAh'},
    ];
    return (
        <Container className='mt-3'>
            <Row>
                <Col md={4}>
                    <h2 className='text-center'>{device.name}</h2>
                    <Image className="mx-auto d-block" width={300} height={300} src={device.img} />
                </Col>

                <Col md={4}>
                    <Card
                    className='d-flex flex-column align-items-center justify-content-around'
                    style={{width: 300, height: 300, fontSize: 32, border: '5px solid lightgray'}}
                    >
                        <h3>От: {device.price} руб.</h3>
                        <Button variant={'outline-dark'}>Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>

            <Row>
                {description.map(info => 
                    <Row>
                        {info.title}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DevicePage;
