import React, { useEffect, useState, useContext } from 'react';
import { Container, Col, Image, Row, Button, Card } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchOneDevice, fetchBrands, deleteDevice } from '../http/deviceAPI';
import { addToBasket } from '../http/basketAPI';
import { Context } from '../index';

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const { user } = useContext(Context);
    const navigate = useNavigate();

    useEffect(() => {
        const load = async () => {
            try {
                const deviceData = await fetchOneDevice(id);
                const brands = await fetchBrands();
                const brand = brands.find(b => b.id === deviceData.brandId);

                setDevice({ ...deviceData, brand });
            } catch (error) {
                console.error('Ошибка при загрузке устройства:', error);
            }
        };

        load();
    }, [id]);

    const handleDelete = async () => {
        if (window.confirm('Вы точно хотите удалить этот товар?')) {
            try {
                await deleteDevice(id);
                alert('Товар успешно удалён');
                navigate('/'); // Вернуться в магазин
            } catch (error) {
                alert('Ошибка при удалении товара');
                console.error(error);
            }
        }
    };

    return (
        <Container
            fluid
            style={{
                maxWidth: 1200,
                margin: '0 auto',
                paddingTop: '4rem',
                paddingBottom: '4rem',
                backgroundColor: '#f8f9fa', // Light gray background
            }}
        >
            {/* Main content row */}
            <Row className="gx-5 align-items-center mb-5">
                {/* Image and Product Info */}
                <Col xs={12} md={6} className="text-center">
                    <Image
                        src={process.env.REACT_APP_API_URL + device.img}
                        alt={device.name}
                        fluid
                        style={{
                            maxHeight: 400,
                            borderRadius: '1rem',
                            boxShadow: '0 0.5rem 1rem rgba(0, 0, 0, 0.1)',
                            objectFit: 'contain',
                        }}
                    />
                    <div className="mt-4">
                        <h1 style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#343a40' }}>
                            {device.name}
                        </h1>
                        <p className="text-muted" style={{ fontSize: '1.2rem' }}>
                            Бренд: {device.brand?.name}
                        </p>
                    </div>
                </Col>

                {/* Pricing and Actions */}
                <Col xs={12} md={6}>
                    <Card
                        className="p-4"
                        style={{
                            borderRadius: '1rem',
                            boxShadow: '0 0.5rem 1rem rgba(0, 0, 0, 0.05)',
                            border: 'none',
                        }}
                    >
                        <h2 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#28a745', marginBottom: '1.5rem' }}>
                            Цена: {device.price} &#8381;
                        </h2>
                        <Button
                            variant="success"
                            size="lg"
                            className="mb-3"
                            onClick={async () => {
                                if (!user.isAuth) {
                                    alert('Сначала войдите в аккаунт, чтобы добавить товар в корзину');
                                    return;
                                }
                                try {
                                    await addToBasket(device.id);
                                    alert('Товар добавлен в корзину');
                                } catch (error) {
                                    alert('Ошибка при добавлении в корзину');
                                }
                            }}
                        >
                            Добавить в корзину
                        </Button>

                        {user.user?.role === 'ADMIN' && (
                            <div className="d-grid gap-2">
                                <Button
                                    variant="outline-secondary"
                                    onClick={() => navigate(`/edit-device/${device.id}`)}
                                >
                                    Редактировать товар
                                </Button>
                                <Button variant="danger" onClick={handleDelete}>
                                    Удалить товар
                                </Button>
                            </div>
                        )}
                    </Card>
                </Col>
            </Row>

            {/* Characteristics */}
            <Row style={{ marginTop: '3rem' }}>
                <Col md={8} className="mx-auto">
                    <h2 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '1.5rem', textAlign: 'center' }}>
                        Характеристики
                    </h2>
                    <Card
                        style={{
                            borderRadius: '1rem',
                            boxShadow: '0 0.5rem 1rem rgba(0, 0, 0, 0.05)',
                            border: 'none',
                        }}
                    >
                        <ul className="list-group list-group-flush">
                            {device.info.map((info) => (
                                <li
                                    className="list-group-item d-flex justify-content-between align-items-center"
                                    key={info.id}
                                >
                                    <span style={{ fontWeight: 'bold' }}>{info.title}:</span>
                                    <span>{info.description}</span>
                                </li>
                            ))}
                        </ul>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default DevicePage;
