import React, { useContext } from 'react';
import { Navbar, Nav, Button, Container } from 'react-bootstrap';
import { Context } from '../index';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';
import { ADMIN_ROUTE, LOGIN_ROUTE, SHOP_ROUTE, BASKET_ROUTE, REGISTRATION_ROUTE } from '../utils/consts';
import { observer } from 'mobx-react-lite';

const NavBar = observer(() => {
    const { user, device } = useContext(Context);
    const navigate = useNavigate();
    const location = useLocation();

    const logOut = () => {
        user.setUser({});
        user.setIsAuth(false);
        localStorage.removeItem('token');
        navigate(SHOP_ROUTE); // Redirect to shop after logout
    };

    const handleHomeClick = () => {
        if (location.pathname === LOGIN_ROUTE || location.pathname === REGISTRATION_ROUTE) {
            return; // Prevent navigation if on login or registration page
        }
        device.resetFilters();
        navigate(SHOP_ROUTE);
    };

    const isAuthPage = location.pathname === LOGIN_ROUTE || location.pathname === REGISTRATION_ROUTE;

    return (
        <Navbar bg="primary" variant="dark" expand="lg" className="shadow-sm">
            <Container>
                <Navbar.Brand
                    style={{ cursor: 'pointer', fontWeight: 'bold', fontSize: '1.5rem' }}
                    onClick={handleHomeClick}
                >
                    ТехноСфера
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="ms-auto">
                        {user.isAuth ? (
                            <>
                                {user.user.role === 'ADMIN' && (
                                    <Button
                                        variant="outline-light"
                                        className="me-2"
                                        onClick={() => navigate(ADMIN_ROUTE)}
                                    >
                                        Админ Панель
                                    </Button>
                                )}
                                <Button
                                    variant="outline-light"
                                    className="me-2"
                                    onClick={() => navigate(BASKET_ROUTE)}
                                >
                                    Корзина
                                </Button>
                                <Button variant="danger" onClick={logOut}>
                                    Выйти
                                </Button>
                            </>
                        ) : (
                            <Button variant="outline-light" onClick={() => navigate(LOGIN_ROUTE)}>
                                Авторизация
                            </Button>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
});

export default NavBar;
