import {makeAutoObservable} from 'mobx';

export default class DeviceStore {
    constructor() {
        this._types = [
            {id: 1, name: 'Холодильники'},
            {id: 2, name: 'Стиральные машины'},
            {id: 3, name: 'Электрочайники'},
            {id: 4, name: 'Миксеры'}
        ]
        this._brands = [
            {id: 1, name: 'Samsung'},
            {id: 2, name: 'Bosch'},  
            {id: 2, name: 'Bosch'},  
        ]
        this._devices = [
            {id: 1, name: 'KSV36AWEP', price: '127999', img: `0aeb8782-5544-4d2e-bce8-c6755a5040eb.jpg`}, 
            {id: 2, name: 'WW60AG4S00CELP', price: '36999', img: `e4748566-7c4d-45f2-b0cd-173a30d43a20.jpg`}, 
            {id: 3, name: 'RB31FERNDWW', price: '42299', img: `14e50c20-7a54-4624-930d-74187d4c54b1.jpg`}, 
            {id: 4, name: 'WAN24200ME', price: '66999', img: `50a4ef32-76bc-45c4-8934-0424a8f72d9c.jpg`}
        ]

        this._selectedType = {};
        this._selectedBrand = {};

        makeAutoObservable(this);
    }

    setTypes(types) {
        this._types = types; 
    }
     
    setBrands(brands) {
        this._brands = brands; 
    }

    setDevices(devices) {
        this._devices = devices; 
    }

    setSelectedType(type) {
        this._selectedType = type;
    }

    setSelectedBrand(brand) {
        this._selectedBrand = brand;
    }

    get types() {
        return this._types;
    }

    get brands() {
        return this._brands;
    }
    
    get devices() {
        return this._devices;
    }

    get selectedType() {
        return this._selectedType;
    }

    get selectedBrand() {
        return this._selectedBrand;
    }
}
