import React, { useEffect, useState, useContext } from 'react';
import { Container, Col, Image, Row, Button, Card } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchOneDevice, fetchBrands, deleteDevice } from '../http/deviceAPI';
import { addToBasket } from '../http/basketAPI';
import { Context } from '../index';

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const { id } = useParams();
    const { user } = useContext(Context);
    const navigate = useNavigate();

    useEffect(() => {
        const load = async () => {
            try {
                const deviceData = await fetchOneDevice(id);
                const brands = await fetchBrands();
                const brand = brands.find(b => b.id === deviceData.brandId);

                setDevice({ ...deviceData, brand });
            } catch (error) {
                console.error('Ошибка при загрузке устройства:', error);
            }
        };

        load();
    }, [id]);

    const handleDelete = async () => {
        if (window.confirm('Вы точно хотите удалить этот товар?')) {
            try {
                await deleteDevice(id);
                alert('Товар успешно удалён');
                navigate('/'); // Вернуться в магазин
            } catch (error) {
                alert('Ошибка при удалении товара');
                console.error(error);
            }
        }
    };

    return (
        <Container className="mt-5">
            <Row>
                <Col md={6}>
                    <div className="d-flex flex-column align-items-center">
                        <h2 className="text-center mb-3">
                            {device.name}
                        </h2>
                        <p className="text-center text-muted">Бренд: {device.brand?.name}</p>
                        <Image
                            className="mx-auto d-block"
                            width={400}
                            height={400}
                            src={process.env.REACT_APP_API_URL + device.img}
                            style={{ borderRadius: '0.75rem', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}
                        />
                    </div>
                </Col>

                <Col md={6}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around p-3"
                        style={{
                            width: '100%',
                            height: '100%',
                            fontSize: 24,
                            border: '1px solid #e0e7ff',
                            borderRadius: '0.75rem',
                            boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
                        }}
                    >
                        <h3 style={{ fontWeight: '600', color: '#374151' }}>От: {device.price} руб.</h3>

                        <Button
                            variant="outline-primary"
                            style={{ padding: '0.75rem 1.5rem', fontSize: '1.1rem' }}
                            onClick={async () => {
                                if (!user.isAuth) {
                                    alert('Сначала войдите в аккаунт, чтобы добавить товар в корзину');
                                    return;
                                }

                                try {
                                    await addToBasket(device.id);
                                    alert('Товар добавлен в корзину');
                                } catch (error) {
                                    alert('Ошибка при добавлении в корзину');
                                }
                            }}
                        >
                            Добавить в корзину
                        </Button>


                        {user.user?.role === 'ADMIN' && (
                            <div className="d-grid gap-2">
                                <Button
                                    variant="outline-secondary"
                                    onClick={() => navigate(`/edit-device/${device.id}`)}
                                >
                                    Редактировать товар
                                </Button>

                                <Button
                                    variant="danger"
                                    onClick={handleDelete}
                                >
                                    Удалить товар
                                </Button>
                            </div>
                        )}
                    </Card>
                </Col>
            </Row>

            <Row className="mt-5">
                <Col>
                    <h2 className="mb-4">Характеристики</h2>
                    {device.info.length > 0 ? (
                        device.info.map((info, index) => (
                            <Row
                                key={info.id}
                                style={{
                                    backgroundColor: index % 2 === 0 ? '#f8f9fa' : 'transparent',
                                    padding: '0.75rem',
                                    borderRadius: '0.5rem',
                                }}
                            >
                                <Col md={4} style={{ fontWeight: '500', color: '#4b5563' }}>{info.title}:</Col>
                                <Col md={8}>{info.description}</Col>
                            </Row>
                        ))
                    ) : (
                        <p>Нет характеристик для данного устройства.</p>
                    )}
                </Col>
            </Row>
        </Container>
    );
};

export default DevicePage;
