import React, { useEffect, useState } from 'react';
import { fetchBasket, removeFromBasket } from '../http/basketAPI';
import { Container, Row, Col, Button, Card, Image } from 'react-bootstrap';

const Basket = () => {
    const [basketDevices, setBasketDevices] = useState([]);

    useEffect(() => {
        fetchBasket().then(data => setBasketDevices(data));
    }, []);

    const handleRemove = async (id) => {
        await removeFromBasket(id);
        setBasketDevices(prev => prev.filter(item => item.id !== id));
    };

    return (
        <Container className="mt-3">
            <h2>Корзина</h2>
            {basketDevices.length === 0 && <p>Корзина пуста</p>}
            {basketDevices.map(item =>
                <Card key={item.id} className="mb-3 p-3">
                        <Row key={device.id} className="align-items-center mb-3">
      <Col md={2}>
        <Image src={process.env.REACT_APP_API_URL + device.img} width={80} height={80} />
      </Col>
      <Col md={6}>
        <h5>{device.name}</h5>
        <p>{device.price} руб.</p>
      </Col>
      <Col md={4}>
        <Button variant="outline-danger" onClick={() => handleRemove(device.id)}>Удалить</Button>
      </Col>
    </Row>
                </Card>
            )}
        </Container>
    );
};

export default Basket;
