import {makeAutoObservable} from 'mobx';

export default class UserStore {
    constructor() {
        this._isAuth = false;
        this._user = {};
        makeAutoObservable(this);
    }

    setIsAuth(bool) {
        this._isAuth = bool; 
    }
     
    setUser(user) {
        this._user = user; 
    }

    get isAuth() {
        return this._isAuth
    }

    get user() {
        return this._user
    }

    isAuth = false;
isLoading = true;

checkAuth = async () => {
    try {
        // например, проверка токена на сервере
        const response = await axios.get('/api/auth/check');
        this.isAuth = true;
    } catch (e) {
        this.isAuth = false;
    } finally {
        this.isLoading = false;
    }
}
}
